using System;
using System.Collections.Generic;
using System.Linq;
using System.Text;
using System.Threading.Tasks;

namespace problem_008
{
    internal class Program
    {
        static void Main(string[] args)
        {
            string series = "";

            char[] characters = series.ToCharArray();
            long[] numbers = characters.Select(x => long.Parse(x.ToString())).ToArray();

            long[] products = new long[1000];
            int a = 0, b = 1, c = 2, d = 3, e = 4, f = 5, g = 6, h = 7, j = 8, k = 9, l = 10, m = 11, n = 12;

            long test = 0;
            for (int i = 0; i <= numbers.Length; i++)
            {
                if (n == 999) break;
                long product = numbers[a] * numbers[b] * numbers[c] * numbers[d] * numbers[e] * numbers[f] * numbers[g] * numbers[h] * numbers[j] * numbers[k] * numbers[l] * numbers[m] * numbers[n];

                a++; b++; c++; d++; e++; f++; g++; h++; j++; k++; l++; m++; n++;

                if (product > test)
                {
                    test = product;
                }

            }
            Console.WriteLine(test);
            Console.ReadKey();
        }
    }
}
